<script setup lang="ts">
import { reactive, ref, computed } from 'vue'
import { useVuelidate } from '@vuelidate/core'
import { mustBeChecked, Required, mLength, SameAsPassword } from '../../utils/validator'
import { useRouter } from 'vue-router'
import axios from 'axios'
import { url_api } from '../../utils/helper'
import { useAlertStore } from '../../stores/alertStore'
import { useAuthStore } from '../../stores/authStore'
import { vMaska } from "maska/vue"

const state = reactive({
    name: 'Дастан',
    phone: '77066011100',
    password: '12345678',
    password_confirmation: '12345678',
    agreement: true
})

const router = useRouter()
const alertStore = useAlertStore()
const authStore = useAuthStore()
const dialog = ref(false)
const visible = ref(false)

const passwordRef = computed(() => state.password)
const rules = {
    name: {
        Required
    },
    phone: {
        Required
    },
    password: {
        Required,
        mLength: mLength(6)
    },
    password_confirmation: {
        Required,
        mLength: mLength(6),
        sameAsPassword: SameAsPassword(passwordRef)
    },
    agreement: {
        mustBeChecked
    }
}

const v$ = useVuelidate(rules, state)

function gotoLogin () {
    router.push({ name: 'login' })
}

function register(){
    if (v$.value.$invalid) {
        v$.value.$touch()
        return
    }

    axios.post(`${url_api}user/register`, state)
        .then((response) => {
            localStorage.setItem('token', response.data.token)
            localStorage.setItem('phone', state.phone)
            authStore.login()
            router.push({ name: 'home' })
        })
        .catch((error) => {
            if(error.response.data.errors) {
                for (const [key, value] of Object.entries(error.response.data.errors)) {
                    alertStore.showAlert(value[0], 'error', 'mdi-alert-circle')
                    v$.value[key].$touch()
                }
            }else{
                alertStore.showAlert(error.response.data.message, 'error', 'mdi-alert-circle')
            }
        })
}

</script>
